          >
            {/* Card header */}
            <div className="p-8 pb-4 flex-none h-[180px]">
              <h2 className="text-3xl font-bold text-center text-indigo-700 mb-6">
                Personalized Meal Plan
                <br />
                Quiz
              </h2>

              {/* Progress */}
