                  <h3 className="text-lg font-semibold mb-6">Personal Details</h3>

                  {/* Height */}
                  <div className="mb-6">
                    <div className="flex justify-between mb-1 text-sm font-medium text-gray-800">
                      <span>üìè Height (cm)</span>
                      <span className="text-indigo-600 font-semibold">
                        {form.height} cm
                      </span>
                    </div>
                    <input
                      type="range"
                      name="height"
                      min={50}
                      max={250}
                      value={form.height}
                      onChange={handleChange}
                      className="w-full h-2 bg-indigo-200 rounded-lg cursor-pointer accent-indigo-600"
                    />
                  </div>

                  {/* Weight */}
                  <div className="mb-6">
                    <div className="flex justify-between mb-1 text-sm font-medium text-gray-800">
                      <span>üèãÔ∏è‚Äç‚ôÄÔ∏è Weight (kg)</span>
                      <span className="text-indigo-600 font-semibold">
                        {form.weight} kg
                      </span>
                    </div>
                    <input
                      type="range"
                      name="weight"
                      min={20}
                      max={200}
                      value={form.weight}
                      onChange={handleChange}
                      className="w-full h-2 bg-indigo-200 rounded-lg cursor-pointer accent-indigo-600"
                    />
                  </div>

                  {/* Age */}
                  <div className="mb-6">
                    <div className="flex justify-between mb-1 text-sm font-medium text-gray-800">
                      <span>üìÖ Age</span>
                      <span className="text-indigo-600 font-semibold">
                        {form.age} years
                      </span>
                    </div>
                    <input
                      type="range"
                      name="age"
                      min={5}
                      max={90}
                      value={form.age}
                      onChange={handleChange}
                      className="w-full h-2 bg-indigo-200 rounded-lg cursor-pointer accent-indigo-600"
                    />
                  </div>

                  {/* Gender */}
                  <div className="mb-6">
                    <label className="block mb-2 text-sm font-medium text-gray-700">
                      Gender
                    </label>
                    <div className="flex gap-4">
                      {["male", "female"].map((g) => (
                        <button
                          key={g}
                          type="button"
                          onClick={() => setForm({ ...form, gender: g })}
                          className={`flex-1 border rounded-2xl p-2 text-center text-md font-medium transition-all ${
