// src/pages/Layout.js
import React, { useEffect, useState } from "react";
import { Outlet, useNavigate } from "react-router-dom";
import { fetchUserSession } from "../services/homeService";
import { logoutUser } from "../services/authService";

const Layout = () => {
  const [name, setName] = useState("Loading...");
  const navigate = useNavigate();

  useEffect(() => {
    (async () => {
      try {
        const user = await fetchUserSession();
        setName(user.name || "Guest");
      } catch {
        setName("Guest");
      }
    })();
  }, []);

  const handleNav = async (path) => {
    if (path === "/logout") {
      try {
        await logoutUser();
      } finally {
        navigate("/auth");
      }
    } else {
      navigate(path);
    }
  };

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <h3>{name}</h3>
        <ul style={styles.navList}>
          <li onClick={() => handleNav("/profileManagement")}>Profile Management</li>
          <li onClick={() => handleNav("/dashboard")}>Dashboard</li>
          <li onClick={() => handleNav("/grocery")}>Grocery</li>
          <li onClick={() => handleNav("/notifications")}>Notifications</li>
          <li onClick={() => handleNav("/feedback")}>Feedback</li>
          <li onClick={() => handleNav("/logout")}>Logout</li>
        </ul>
      </aside>
      <main style={styles.main}>
        <Outlet /> {/* This renders the active page content */}
      </main>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    minHeight: "100vh",
  },
  sidebar: {
    width: "200px",
    backgroundColor: "#f5f5f5",
    padding: "20px",
    borderRight: "1px solid #ddd",
  },
  navList: {
    listStyle: "none",
    padding: 0,
    marginTop: "20px",
    lineHeight: "2",
    fontSize: "16px",
    cursor: "pointer",
  },
  main: {
    flex: 1,
    padding: "40px",
  },
};

export default Layout;
