// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import {
  fetchUserSession,
  fetchStoredMealPlan,
  generateMealPlan,
  generateGroceryList,
  generateInstructions,
} from "../services/homeService";

const dayNames = [
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
  "Sunday",
];

function Dashboard() {
  const [name, setName] = useState("");
  const [loading, setLoading] = useState(false);
  const [structuredPlan, setStructuredPlan] = useState([]);
  const [mealHistory, setMealHistory] = useState([]);
  const [expanded, setExpanded] = useState(null);

  const getWeekStart = (d) => {
    const date = new Date(d);
    const day = (date.getDay() + 6) % 7; // Monday=0
    date.setHours(0, 0, 0, 0);
    date.setDate(date.getDate() - day);
    return date.toISOString().slice(0, 10);
  };

  const todayKey = getWeekStart(new Date());

  useEffect(() => {
    (async () => {
      try {
        const user = await fetchUserSession();
        setName(user.name || "Guest");

        const stored = await fetchStoredMealPlan();
        if (stored.plan && Array.isArray(stored.plan)) {
          setStructuredPlan(stored.plan);
        }
        if (Array.isArray(stored.history)) {
          setMealHistory(stored.history);
        }
      } catch (err) {
        console.error("Session fetch error:", err);
        setName("Guest");
      }
    })();
  }, []);

  const handleGenerate = async () => {
    setLoading(true);
    try {
      const data = await generateMealPlan();
      if (data.plan && Array.isArray(data.plan)) {
        setStructuredPlan(data.plan);

      }

      await generateInstructions();
      await generateGroceryList();

      const updated = await fetchStoredMealPlan();
      if (Array.isArray(updated.history)) {
        setMealHistory(updated.history);
      }
    } catch (err) {
      console.error("Generation error:", err.message);
    }
    setLoading(false);
  };

  const weeks = [
    new Date(new Date(todayKey).setDate(new Date(todayKey).getDate() - 7)),
    new Date(todayKey),
    new Date(new Date(todayKey).setDate(new Date(todayKey).getDate() + 7))
  ].map(d => getWeekStart(d));

  const plansByWeek = mealHistory.reduce((acc, w) => {
    if (w.startDate) {
      acc[getWeekStart(w.startDate)] = w;
    }
    return acc;
  }, {});

  return (
    <div style={styles.wrapper}>
      <div style={styles.header}>
        <h1 style={styles.title}>Welcome, {name}</h1>
        <p style={styles.subtitle}>Let’s get cookin’ 🍳</p>
      </div>

      {weeks.map((wk) => {
        const week = plansByWeek[wk] || {};
        const plan = week.plan || [];
        const instructions = week.instructions || {};
        return (
          <div key={wk} style={styles.calendar}>
            <h3 style={styles.weekHeader}>Week of {wk}</h3>
            {dayNames.map((dayName, idx) => {
              const meals = plan[idx] || {};
              const instrDay = instructions[`Day ${idx + 1}`] || {};
              return (
                <div key={idx} style={styles.row}>
                  <div style={styles.dayName}>{dayName}</div>
                  <div style={styles.mealsColumn}>
                    {Object.entries(meals)
                      .filter(([k]) => k.toLowerCase() !== "_id")
                      .map(([mealType, description]) => {
                        const boxKey = `${wk}-${idx}-${mealType}`;
                        const isOpen = expanded === boxKey;
                        return (
                          <div
                            key={mealType}
                            style={styles.mealBox}
                            onClick={() =>
                              setExpanded(isOpen ? null : boxKey)
                            }
                          >
                            <strong>{mealType}:</strong> {description}
                            {isOpen && instrDay[mealType] && (
                              <div style={styles.instructions}>{instrDay[mealType]}</div>
                            )}
                          </div>
                        );
                      })}
                  </div>
                </div>
              );
            })}
          </div>
        );
      })}

      {mealHistory.length === 0 && (
        <>
          <button onClick={handleGenerate} style={styles.button}>
            Generate Meal Plan
          </button>
          {loading && <p>Generating your meal plan... 🍽️</p>}
        </>
      )}
    </div>
  );
}

const styles = {
  wrapper: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    paddingTop: "50px",
    minHeight: "100vh",
    backgroundColor: "#fff",
  },
  header: {
    textAlign: "center",
    marginBottom: "40px",
  },
  title: {
    fontSize: "2.2rem",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "1.2rem",
    fontStyle: "italic",
    color: "#777",
  },
  button: {
    padding: "12px 24px",
    fontSize: "16px",
    marginTop: "20px",
  },
  calendar: {
    display: "flex",
    flexDirection: "column",
    width: "80%",
    maxWidth: "900px",
    borderTop: "1px solid #ddd",
    borderLeft: "1px solid #ddd",
  },
  row: {
    display: "flex",
    borderBottom: "1px solid #ddd",
    minHeight: "80px",
  },
  dayName: {
    width: "120px",
    padding: "15px",
    backgroundColor: "#f8f8f8",
    fontWeight: "bold",
    borderRight: "1px solid #ddd",
    textAlign: "center",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  mealsColumn: {
    flex: 1,
    display: "flex",
    flexWrap: "wrap",
    alignItems: "center",
    padding: "10px 15px",
    gap: "15px",
  },
  mealBox: {
    background: "#f0f4ff",
    padding: "10px 14px",
    borderRadius: "8px",
    boxShadow: "0 2px 6px rgba(0, 0, 0, 0.05)",
    minWidth: "200px",
    cursor: "pointer",
  },
  instructions: {
    marginTop: "8px",
    fontSize: "0.9rem",
    color: "#333",
  },
  weekHeader: {
    background: "#fafafa",
    padding: "10px",
    borderBottom: "1px solid #ddd",
    margin: 0,
  },
};

export default Dashboard;
