import React, { useEffect, useState } from "react";
import { fetchStoredMealPlan } from "../services/homeService";

function Grocery() {
  const [grocery, setGrocery] = useState({});

  useEffect(() => {
    (async () => {
      try {
        const stored = await fetchStoredMealPlan();
        if (stored.groceryList) {
          setGrocery(stored.groceryList);
        }
      } catch (err) {
        console.error("Failed to load grocery list", err);
      }
    })();
  }, []);

  const hasGrocery = Object.keys(grocery).length > 0;

  return (
    <div style={styles.container}>
      <h2>Grocery List</h2>
      {!hasGrocery && <p>No grocery list generated yet.</p>}
      {hasGrocery &&
        Object.entries(grocery).map(([category, items]) => (
          <div key={category} style={styles.section}>
            <h3>{category}</h3>
            <ul>
              {items.map((entry, idx) => (
                <li key={idx}>
                  {entry.item} ({entry.quantity})
                </li>
              ))}
            </ul>
          </div>
        ))}
    </div>
  );
}

const styles = {
  container: { padding: 20, maxWidth: 600, margin: "auto" },
  section: { marginBottom: 20 },
};

export default Grocery;
