import React, { useEffect, useState } from "react";
import { fetchProfile, updateName, updateSubscription, updateQuizData } from "../services/profileService";

function ProfileManagement() {
  const [profile, setProfile] = useState(null);
  const [nameInput, setNameInput] = useState("");
  const initialQuiz = {
    height: "",
    weight: "",
    age: "",
    reason: "",
    mealType: "",
    skillLevel: "",
    mealsPerDay: [],
    restrictions: [],
    cuisine: [],
  };
  const [quizForm, setQuizForm] = useState(initialQuiz);

  useEffect(() => {
    (async () => {
      try {
        const data = await fetchProfile();
        setProfile(data);
        setNameInput(data.name || "");
        setQuizForm(data.quizData || initialQuiz);
      } catch (err) {
        console.error("Profile fetch error:", err.message);
      }
    })();
  }, []);

  const handleSaveName = async () => {
    try {
      const res = await updateName(nameInput);
      setProfile((prev) => ({ ...prev, name: res.name }));
    } catch (err) {
      console.error("Name update error:", err.message);
    }
  };

  const handleSubscriptionChange = async (e) => {
    const sub = e.target.value;
    try {
      await updateSubscription(sub);
      setProfile((prev) => ({ ...prev, subscription: sub }));
    } catch (err) {
      console.error("Subscription update error:", err.message);
    }
  };

  const handleQuizChange = (e) => {
    setQuizForm({ ...quizForm, [e.target.name]: e.target.value });
  };

  const handleQuizCheckbox = (name, value) => {
    setQuizForm((prev) => {
      const set = new Set(prev[name]);
      set.has(value) ? set.delete(value) : set.add(value);
      return { ...prev, [name]: [...set] };
    });
  };

  const handleSaveQuizData = async () => {
    try {
      const res = await updateQuizData(quizForm);
      setProfile((prev) => ({ ...prev, quizData: res.quizData }));
    } catch (err) {
      console.error("Quiz data update error:", err.message);
    }
  };

  if (!profile) return <div style={styles.container}>Loading...</div>;

  return (
    <div style={styles.container}>
      <h2>Profile Management</h2>

      <div style={styles.section}>
        <label>Name:</label>
        <input
          type="text"
          value={nameInput}
          onChange={(e) => setNameInput(e.target.value)}
          onBlur={handleSaveName}
          style={styles.input}
        />
      </div>

      <div style={styles.section}>
        <label>Subscription:</label>
        <select
          value={profile.subscription}
          onChange={handleSubscriptionChange}
          style={styles.input}
        >
          <option value="free">Free</option>
          <option value="premium">Premium</option>
        </select>
      </div>

      <div style={styles.section}>
        <h3>Profile Details</h3>
        <label>Height (cm):</label>
        <input
          type="number"
          name="height"
          value={quizForm.height}
          onChange={handleQuizChange}
          style={styles.input}
        />
        <label>Weight (kg):</label>
        <input
          type="number"
          name="weight"
          value={quizForm.weight}
          onChange={handleQuizChange}
          style={styles.input}
        />
        <label>Age:</label>
        <input
          type="number"
          name="age"
          value={quizForm.age}
          onChange={handleQuizChange}
          style={styles.input}
        />
        <label>Reason:</label>
        <textarea
          name="reason"
          value={quizForm.reason}
          onChange={handleQuizChange}
          style={styles.input}
        />
        <label>Meal Type:</label>
        <select
          name="mealType"
          value={quizForm.mealType}
          onChange={handleQuizChange}
          style={styles.input}
        >
          <option value="">--Choose--</option>
          <option value="vegan">Vegan</option>
          <option value="balanced">Balanced</option>
          <option value="vegetarian">Vegetarian</option>
        </select>
        <label>Skill Level:</label>
        {["basic", "intermediate", "expert"].map((level) => (
          <label key={level} style={{ display: "block" }}>
            <input
              type="radio"
              name="skillLevel"
              value={level}
              checked={quizForm.skillLevel === level}
              onChange={handleQuizChange}
              style={{ marginRight: 8 }}
            />
            {level.charAt(0).toUpperCase() + level.slice(1)}
          </label>
        ))}
        <label>Meals Per Day:</label>
        {["Breakfast", "Lunch", "Dinner"].map((meal) => (
          <label key={meal} style={{ display: "block" }}>
            <input
              type="checkbox"
              value={meal}
              checked={quizForm.mealsPerDay.includes(meal)}
              onChange={() => handleQuizCheckbox("mealsPerDay", meal)}
              style={{ marginRight: 8 }}
            />
            {meal}
          </label>
        ))}
        <label>Restrictions:</label>
        {["nuts", "gluten", "dairy", "soy", "eggs"].map((item) => (
          <label key={item} style={{ display: "block" }}>
            <input
              type="checkbox"
              value={item}
              checked={quizForm.restrictions.includes(item)}
              onChange={() => handleQuizCheckbox("restrictions", item)}
              style={{ marginRight: 8 }}
            />
            {item}
          </label>
        ))}
        <label>Cuisine:</label>
        {["indian", "italian", "thai", "mexican", "japanese", "mediterranean", "chinese"].map((cuisine) => (
          <label key={cuisine} style={{ display: "block" }}>
            <input
              type="checkbox"
              value={cuisine}
              checked={quizForm.cuisine.includes(cuisine)}
              onChange={() => handleQuizCheckbox("cuisine", cuisine)}
              style={{ marginRight: 8 }}
            />
            {cuisine.charAt(0).toUpperCase() + cuisine.slice(1)}
          </label>
        ))}
        <button onClick={handleSaveQuizData}>Save Details</button>
      </div>
    </div>
  );
}

const styles = {
  container: { padding: 20, maxWidth: 600, margin: "auto" },
  section: { marginBottom: 20 },
  input: { display: "block", padding: 8, marginBottom: 10, width: "100%", maxWidth: 300 },
};

export default ProfileManagement;
